import ZoneList from './main'
import React from "react";
import axios from "axios";
import BootstrapTable from "react-bootstrap-table-next";
import TableHeaderColumn from "react-bootstrap-table-next";
import ToolkitProvider from "react-bootstrap-table2-toolkit";
import paginationFactory from "react-bootstrap-table2-paginator";
import cellEditFactory from "react-bootstrap-table2-editor";


import {
  Button,
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Col,
  Container,
  CustomInput,
  Form,
  FormGroup,
  FormText,
  Input,
  InputGroup,
  InputGroupAddon,
  Label,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Row,
  Table
} from "reactstrap";






class Zone extends React.Component {
  constructor(props) {
        super(props);

        this.state = {
            activeItem: this.props.activeItem,
            zones: []
        };
    }

    refreshList = () => {
        axios
          .get("http://localhost:8000/api/zone/")
          .then(res => this.setState({ zones: res.data }))
          .catch(err => console.log(err));
    };

//Allways called when component initializes
    componentDidMount() {

        axios.get("http://localhost:8000/api/zone/")
          .then(res => {
            const zones = res.data;
            this.setState({ zones });

            //Find the passed property in the zone array and set it to the active item
            this.state.activeItem = this.state.zones.find(element => element.id === this.props.location.state.id)
            console.log(this.state.activeItem)
          })


    }

    //Edit the last item of a table
    editItem = item => {

        //Check if the user operates on the last row; This is in order to add requirements directly in the last row
        //of the table
        if(this.state.zones[this.state.zones.length - 1].id === item.id)
        {

            console.log(item)
            console.log(this.state.zones[this.state.zones.length - 1].id)

            axios
                .put(`http://localhost:8000/api/zone/${item.id}/`, item)
                .then(res => this.refreshList());


            const itemToBeSubmitted = {
                    title: " ",
                    description: " ",
                    completed: false
             };

            axios
              .post("http://localhost:8000/api/zone/", itemToBeSubmitted)
              .then(res => this.refreshList());
        }
    };

    //Call the edit item function in order to edit a cell of a table (in this case the last element
    cellEdit = cellEditFactory({

        mode: 'click',
        blurToSave: true,
        afterSaveCell: (oldValue, newValue, row, column) => {
            this.editItem(row)
        }
    });


    Zone = () => {

        ZoneList.logging();
        this.state.activeItem = ZoneList.getItem();
    };


    importZone = () => (



        <div/>
    );

    tableColumns = [
      {
        dataField: "title",
        text: "title",
        sort: true,
      },
      {
        dataField: "description",
        text: "description",
        type: 'textarea' ,
        sort: true,
      },
      {
        dataField: 'df1',
        isDummyField: true,
        text: 'Action 1',
        editable: false,
        formatter: (cellContent, row) => {


          return (
              <h5>

              </h5>
            );
          }
      }
    ];


    componentTable = () => (

        <Card>
          <CardHeader>
            <CardTitle tag="h5">Pagination</CardTitle>
            <h6 className="card-subtitle text-muted">
              Pagination by react-bootstrap-table2
            </h6>
          </CardHeader>
          <CardBody>
            <BootstrapTable
              keyField="id"
              data={this.state.activeItem}
              columns={this.tableColumns}
              deleteRow={ true }
              expandRow={this.expandRow}
              bootstrap4
              cellEdit={ this.cellEdit }
              bordered={false}>
            </BootstrapTable>

          </CardBody>
        </Card>
    );


    Tables = () => (

        <Container fluid className="p-0">

                {this.componentTable()}

        </Container>

    );

    render() {
        return (
            <div>
                {this.Tables()}
            </div>
        )
      }
}

export default Zone;
