import React from "react";
import axios from "axios";
import BootstrapTable from "react-bootstrap-table-next";
import ToolkitProvider from "react-bootstrap-table2-toolkit";
import paginationFactory from "react-bootstrap-table2-paginator";

import { MinusCircle, PlusCircle } from "react-feather";


import {
  Button,
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Col,
  Container,
  CustomInput,
  Form,
  FormGroup,
  FormText,
  Input,
  InputGroup,
  InputGroupAddon,
  Label,
  Row,
  Table
} from "reactstrap";




import { Edit2, Trash } from "react-feather";

import avatar1 from "../../assets/img/avatars/avatar.jpg";
import avatar2 from "../../assets/img/avatars/avatar-2.jpg";
import avatar3 from "../../assets/img/avatars/avatar-3.jpg";
import avatar4 from "../../assets/img/avatars/avatar-4.jpg";


class PersonList extends React.Component {
  constructor() {


        this.state = {
            activeItem: {
                title: "",
                description: "",
                completed: false
            },

            todolist: []
        };

        this.handleSubmit = this.handleSubmit.bind(this);
    }

        refreshList = () => {
            axios
              .get("http://localhost:8000/api/todos/")
              .then(res => this.setState({ todolist: res.data }))
              .catch(err => console.log(err));
        };

      componentDidMount() {
        axios.get("http://localhost:8000/api/todos/")
          .then(res => {
            const todolist = res.data;
            this.setState({ todolist });
          })
      }


        handleSubmit = () => {

            console.log(this.state.activeItem)

            const itemToBeSubmitted = {
                title: this.state.activeItem.title,
                description: this.state.activeItem.description,
                completed: this.state.activeItem.completed
            };


            if (itemToBeSubmitted.id) {
              axios
                .put(`http://localhost:8000/api/todos/${itemToBeSubmitted.id}/`, itemToBeSubmitted)
                .then(res => this.refreshList());
              return;
            }
            axios
              .post("http://localhost:8000/api/todos/", itemToBeSubmitted)
              .then(res => this.refreshList());
        };

        handleChange = e => {
            let { name, value } = e.target;

            if (e.target.type === "checkbox") {
                value = e.target.checked;
            }

            const activeItem = { ...this.state.activeItem, [name]: value };

            this.setState({activeItem });
        };

        handleDelete = item => {

            console.log(item)

            axios
              .delete(`http://localhost:8000/api/todos/${item.id}`)
              .then(res => this.refreshList());
        };



     tableData = [
  {
    name: "Tiger Nixon",
    position: "System Architect",
    office: "Edinburgh",
    age: 61,
    startDate: "2011/04/25",
    salary: "$320,800"
  }];


tableColumns = [
  {
    dataField: "name",
    text: "Name",
    sort: true
  }];


BasicForm = () => (

    <Card>
        <CardHeader>
            <CardTitle tag="h5">Basic form</CardTitle>
            <h6 className="card-subtitle text-muted">
            Default Bootstrap form layout.
            </h6>
        </CardHeader>
        <CardBody>
            <Form onSubmit={this.handleSubmit} selectRow={ this.selectRowProp } >

                <FormGroup>
                  <Label for="title">Title</Label>
                  <Input
                    type="text"
                    name="title"
                    value={this.state.activeItem.title}
                    onChange={this.handleChange}
                    placeholder="Enter Todo Title"
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="description">Description</Label>
                  <Input
                    type="text"
                    name="description"
                    value={this.state.activeItem.description}
                    onChange={this.handleChange}
                    placeholder="Enter Todo description"
                  />
                </FormGroup>
                <FormGroup check>
                  <Label for="completed">
                    <Input
                      type="checkbox"
                      name="completed"
                      checked={this.state.activeItem.completed}
                      onChange={this.handleChange}
                    />
                    Completed
                  </Label>
                </FormGroup>

                <Button color="primary" type="submit">Submit</Button>
            </Form>
        </CardBody>


    </Card>
);

products = [{
      id: 1,
      name: "Product1",
      price: 120
  }, {
      id: 2,
      name: "Product2",
      price: 80
  }];



BasicTable = () => (

    <Card>
       <CardHeader>
        <CardTitle tag="h5">Pagination</CardTitle>
        <h6 className="card-subtitle text-muted">
          Pagination by react-bootstrap-table2
        </h6>
      </CardHeader>
      <CardBody>
        <BootstrapTable
          keyField="name"
          data={this.tableData}
          columns={this.tableColumns}
          bootstrap4
          bordered={false}
          pagination={paginationFactory({
            sizePerPage: 5,
            sizePerPageList: [5, 10, 25, 50]
          })}
        />
      </CardBody>



    </Card>


);


PaginationTable = () => (

    <Card>
      <CardHeader>
        <CardTitle tag="h5">Pagination</CardTitle>
        <h6 className="card-subtitle text-muted">
          Pagination by react-bootstrap-table2
        </h6>
      </CardHeader>
      <CardBody>
        <BootstrapTable
          keyField="name"
          data={this.tableData}
          columns={this.tableColumns}
          bootstrap4
          bordered={false}
          pagination={paginationFactory({
            sizePerPage: 5,
            sizePerPageList: [5, 10, 25, 50]
          })}
        />
      </CardBody>
    </Card>
);


Tables = () => (
  <Container fluid className="p-0">
    <h1 className="h3 mb-3">Advanced Tables</h1>

    {this.BasicForm()}


  </Container>
);

render() {
    return (
        <div>
            {this.Tables()}
        </div>
    )
  }
}

export default PersonList;
