import ZoneList from './main'
import React from "react";
import axios from "axios";
import BootstrapTable from "react-bootstrap-table-next";
import TableHeaderColumn from "react-bootstrap-table-next";
import ToolkitProvider from "react-bootstrap-table2-toolkit";
import paginationFactory from "react-bootstrap-table2-paginator";
import cellEditFactory from "react-bootstrap-table2-editor";


import {
  Button,
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Col,
  Container,
  CustomInput,
  Form,
  FormGroup,
  FormText,
  Input,
  InputGroup,
  InputGroupAddon,
  Label,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Row,
  Table
} from "reactstrap";






class Zone extends React.Component {
  constructor(props) {
        super(props);

        this.state = {
            activeItem: this.props.activeItem,
            zones: []
        };
    }

    refreshList = () => {
        axios
          .get("http://localhost:8000/api/zone/")
          .then(res => this.setState({ zones: res.data }))
          .catch(err => console.log(err));
    };

//Allways called when component initializes
    componentDidMount() {

        axios.get("http://localhost:8000/api/zone/")
          .then(res => {
            const zones = res.data;
            this.setState({ zones });

            //Find the passed property in the zone array and set it to the active item
            this.state.activeItem = this.state.zones.find(element => element.id === this.props.location.state.id)
            console.log(this.state.activeItem)
          })


    }


Zone = () => {

    ZoneList.logging();
    this.state.activeItem = ZoneList.getItem();
};


importZone = () => (



    <div/>
);


Tables = () => (

    <Container fluid className="p-0">
            {this.importZone()}

    </Container>

);

render() {
    return (
        <div>
            {this.Tables()}
        </div>
    )
  }
}

export default Zone;
